                    function(b, c) {
                        e.append(a(c))
                    })) : (e.addClass("hidden").empty(), e.parent().removeClass("hasResult"))
                }
            })
        }
    }),
    e.on("tap", "li",
    function() {
        var a = $(this)[0].innerText;
        c.val(a),
        b.trigger("tap"),
        c.trigger("input"),
        e.addClass("hidden")
    }),
    {
        clearInput: function(a) {
            c.val("").trigger("blur"),
            null != a && a || c.trigger("input")
        }
    }
},
couponCountShow = function() {
    $.ajax({
        url: xinyunlian.base + "/wx/discountCoupon/countCoupon.jhtml",
        type: "get",
        dataType: "JSON",
        cache: !1,
        async: !0,
        success: function(a) {
            "number" == typeof a && (0 == a ? $(".coupon_count").hide() : ($(".coupon_count").show(), $(".coupon_count").html(a > 99 ? 99 : a)))
        }
    })
};
$.onPageShow(function() {
    loadProductAjax ? loadProductAjax.then(function() {
        $categoryList = $("#categoryList"),
        Page.init(),
        Search = SearchInit(),
        couponCountShow(),
        0 == $(".ad .swiper-slide").length && $("#ad").remove();
        var a = $("#searchCouponId").val(); (a || "0" === a) && (PageData.currentPage.type = "searchCouponId", PageData.currentPage.searchCouponId = a, PageData.replaceProduct({
            type: "searchCouponId",
            searchCouponId: a,
            pageNumber: 1
        })),
        backScrollEvent()
    }) : (Page.init(), Search = SearchInit(), couponCountShow(), 0 == $(".ad .swiper-slide").length && $("#ad").remove(), $("#dealerId").val() && Navigation.init(), backScrollEvent())
});
